import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { Download, RefreshCw } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import BatchStats from "@/components/admin/batch-stats";
import StudentTable from "@/components/admin/student-table";
import type { AdminDashboardData } from "@shared/schema";

export default function AdminDashboard() {
  const { toast } = useToast();
  const queryClient = useQueryClient();

  const { data, isLoading, error } = useQuery<AdminDashboardData>({
    queryKey: ['/api/dashboard/admin'],
  });

  const syncMutation = useMutation({
    mutationFn: () => apiRequest('POST', '/api/sync/all'),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard/admin'] });
      toast({
        title: "Sync completed",
        description: "All student data has been updated.",
      });
    },
    onError: () => {
      toast({
        title: "Sync failed",
        description: "Failed to sync student data. Please try again.",
        variant: "destructive",
      });
    },
  });

  const syncProfilePhotosMutation = useMutation({
    mutationFn: () => apiRequest('POST', '/api/sync/profile-photos'),
    onSuccess: (data: any) => {
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard/admin'] });
      toast({
        title: "Profile photos synced",
        description: `Updated ${data.success} profile photos successfully.`,
      });
    },
    onError: () => {
      toast({
        title: "Sync failed",
        description: "Failed to sync profile photos. Please try again.",
        variant: "destructive",
      });
    },
  });

  const handleExportCSV = () => {
    window.open('/api/export/csv', '_blank');
  };

  const handleInitStudents = async () => {
    try {
      await apiRequest('POST', '/api/init-students');
      queryClient.invalidateQueries({ queryKey: ['/api/dashboard/admin'] });
      toast({
        title: "Students initialized",
        description: "Student data has been imported successfully.",
      });
    } catch (error) {
      toast({
        title: "Import failed",
        description: "Failed to import student data.",
        variant: "destructive",
      });
    }
  };

  if (error) {
    return (
      <div className="flex-1 p-6">
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <h3 className="text-red-800 font-medium">Error loading dashboard</h3>
          <p className="text-red-600 text-sm mt-1">
            Failed to load admin data. Please try refreshing or initialize students first.
          </p>
          <Button onClick={handleInitStudents} className="mt-3" variant="outline">
            Initialize Students
          </Button>
        </div>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="flex-1 p-6">
        <div className="animate-pulse space-y-6">
          <div className="h-8 bg-slate-200 rounded w-1/3"></div>
          <div className="grid grid-cols-1 md:grid-cols-4 gap-4">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="h-24 bg-slate-200 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  if (!data || data.totalStudents === 0) {
    return (
      <div className="flex-1 p-6">
        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
          <h3 className="text-yellow-800 font-medium">No students found</h3>
          <p className="text-yellow-600 text-sm mt-1">
            Please initialize the student database first.
          </p>
          <Button onClick={handleInitStudents} className="mt-3">
            Initialize Students
          </Button>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-1 overflow-auto">
      {/* Header */}
      <header className="bg-white border-b border-slate-200 px-6 py-4">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-xl font-semibold text-slate-900">Admin Dashboard</h2>
            <p className="text-sm text-slate-500">Monitor batch performance and manage students</p>
          </div>
          <div className="flex items-center space-x-3">
            <Button 
              onClick={handleExportCSV}
              variant="outline"
              className="text-green-600 border-green-600 hover:bg-green-50"
            >
              <Download className="mr-2" size={16} />
              Export CSV
            </Button>
            <Button 
              onClick={() => syncProfilePhotosMutation.mutate()}
              disabled={syncProfilePhotosMutation.isPending}
              variant="outline"
              className="text-purple-600 border-purple-600 hover:bg-purple-50"
            >
              <RefreshCw className={`mr-2 ${syncProfilePhotosMutation.isPending ? 'animate-spin' : ''}`} size={16} />
              {syncProfilePhotosMutation.isPending ? 'Syncing Photos...' : 'Sync Photos'}
            </Button>
            <Button 
              onClick={() => syncMutation.mutate()}
              disabled={syncMutation.isPending}
              className="bg-blue-600 hover:bg-blue-700"
            >
              <RefreshCw className={`mr-2 ${syncMutation.isPending ? 'animate-spin' : ''}`} size={16} />
              {syncMutation.isPending ? 'Syncing...' : 'Sync All'}
            </Button>
          </div>
        </div>
      </header>

      {/* Dashboard Content */}
      <div className="p-6 space-y-6">
        <Card>
          <CardHeader>
            <div className="flex items-center justify-between">
              <CardTitle>Batch Overview</CardTitle>
            </div>
          </CardHeader>
          <CardContent>
            <BatchStats data={data} />
            <StudentTable data={data} />
          </CardContent>
        </Card>

        {/* Leaderboard */}
        <Card>
          <CardHeader>
            <CardTitle>Weekly Leaderboard</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {data.leaderboard.slice(0, 5).map((entry, index) => (
                <div 
                  key={entry.student.id}
                  className={`flex items-center justify-between p-4 rounded-lg border ${
                    index === 0 ? 'bg-gradient-to-r from-yellow-50 to-orange-50 border-yellow-200' : 'bg-slate-50 border-slate-200'
                  }`}
                >
                  <div className="flex items-center space-x-4">
                    <div className={`w-8 h-8 rounded-full flex items-center justify-center font-bold text-white ${
                      index === 0 ? 'bg-yellow-400' : index === 1 ? 'bg-slate-400' : 'bg-amber-600'
                    }`}>
                      {entry.rank}
                    </div>
                    <img 
                      src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=32&h=32"
                      alt="Student" 
                      className="w-8 h-8 rounded-full object-cover" 
                    />
                    <div>
                      <p className="font-medium text-slate-900">{entry.student.name}</p>
                      <p className="text-xs text-slate-500">@{entry.student.leetcodeUsername}</p>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className="font-bold text-slate-900">+{entry.weeklyScore}</p>
                    <p className="text-xs text-slate-500">this week</p>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/analytics-dashboard.tsx.content:
import { useQuery, useMutation, useQueryClient } from "@tanstack/react-query";
import { useState } from "react";
import { TrendingUp, TrendingDown, Minus, Download, Upload, RefreshCw } from "lucide-react";
import { Button } from "@/components/ui/button";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Table, TableBody, TableCell, TableHead, TableHeader, TableRow } from "@/components/ui/table";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import {
  LineChart,
  Line,
  BarChart,
  Bar,
  PieChart,
  Pie,
  Cell,
  AreaChart,
  Area,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  Legend,
  ResponsiveContainer
} from "recharts";

interface AnalyticsData {
  summaryStats: {
    totalStudents: number;
    improved: number;
    declined: number;
    same: number;
    averageImprovement: number;
  };
  top10Students: any[];
  top15Improvers: any[];
  progressCategories: {
    improved: number;
    declined: number;
    same: number;
  };
  classAverageProgression: any[];
  allStudentsData: any[];
}

const COLORS = {
  improved: '#10B981', // Green
  declined: '#EF4444', // Red  
  same: '#6B7280', // Gray
  primary: '#3B82F6', // Blue
  secondary: '#8B5CF6' // Purple
};

const PIE_COLORS = [COLORS.improved, COLORS.declined, COLORS.same];

export default function AnalyticsDashboard() {
  const { toast } = useToast();
  const queryClient = useQueryClient();
  const [autoRefresh, setAutoRefresh] = useState(false);

  const { data: analyticsData, isLoading, error, refetch } = useQuery<AnalyticsData>({
    queryKey: ['/api/analytics'],
    refetchInterval: autoRefresh ? 30000 : false, // Auto-refresh every 30 seconds if enabled
  });

  const importMutation = useMutation({
    mutationFn: () => apiRequest('POST', '/api/import/csv'),
    onSuccess: (result: any) => {
      queryClient.invalidateQueries({ queryKey: ['/api/analytics'] });
      toast({
        title: "Import successful",
        description: result.message || "CSV data imported successfully",
      });
    },
    onError: (error) => {
      toast({
        title: "Import failed",
        description: "Failed to import CSV data. Please try again.",
        variant: "destructive",
      });
    },
  });

  const syncMutation = useMutation({
    mutationFn: () => apiRequest('POST', '/api/sync/all'),
    onSuccess: () => {
      queryClient.invalidateQueries({ queryKey: ['/api/analytics'] });
      toast({
        title: "Sync completed",
        description: "All student data has been updated with real-time LeetCode data.",
      });
    },
    onError: () => {
      toast({
        title: "Sync failed",
        description: "Failed to sync with LeetCode. Please try again.",
        variant: "destructive",
      });
    },
  });

  if (isLoading) {
    return (
      <div className="flex-1 flex items-center justify-center">
        <div className="text-center">
          <RefreshCw className="mx-auto h-8 w-8 animate-spin text-blue-600 mb-4" />
          <p className="text-lg font-medium">Loading analytics data...</p>
        </div>
      </div>
    );
  }

  if (error || !analyticsData) {
    return (
      <div className="flex-1 flex items-center justify-center">
        <div className="text-center">
          <p className="text-lg font-medium text-red-600 mb-4">Failed to load analytics data</p>
          <Button onClick={() => refetch()} variant="outline">
            <RefreshCw className="mr-2 h-4 w-4" />
            Try Again
          </Button>
        </div>
      </div>
    );
  }

  const formatTrendData = () => {
    return analyticsData.top10Students.map((student) => {
      const weeklyData: any = {};
      student.weeklyTrends.forEach((trend: any, index: number) => {
        weeklyData[`Week ${index + 1}`] = trend?.totalProblems || 0;
      });
      weeklyData['Current'] = student.currentSolved;
      weeklyData['student'] = student.student.name;
      return weeklyData;
    });
  };

  const formatImprovementData = () => {
    return analyticsData.top15Improvers.map((student) => ({
      name: student.student.name.split(' ').slice(0, 2).join(' '), // Shortened name
      improvement: student.improvement,
      improvementPercent: student.improvementPercent
    }));
  };

  const formatPieData = () => {
    return [
      { name: 'Improved', value: analyticsData.progressCategories.improved, color: COLORS.improved },
      { name: 'Declined', value: analyticsData.progressCategories.declined, color: COLORS.declined },
      { name: 'Same', value: analyticsData.progressCategories.same, color: COLORS.same }
    ];
  };

  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'improved':
        return <TrendingUp className="h-4 w-4 text-green-600" />;
      case 'declined':
        return <TrendingDown className="h-4 w-4 text-red-600" />;
      default:
        return <Minus className="h-4 w-4 text-gray-600" />;
    }
  };

  const getStatusBadge = (status: string) => {
    const colors = {
      improved: 'bg-green-100 text-green-800',
      declined: 'bg-red-100 text-red-800',
      same: 'bg-gray-100 text-gray-800'
    };
    return colors[status as keyof typeof colors] || colors.same;
  };

  return (
    <div className="flex-1 overflow-auto">
      {/* Header */}
      <header className="bg-white border-b border-slate-200 px-6 py-4">
        <div className="flex items-center justify-between">
          <div>
            <h2 className="text-xl font-semibold text-slate-900">Real-Time Analytics Dashboard</h2>
            <p className="text-sm text-slate-500">Historical trends and current LeetCode progress analysis</p>
          </div>
          <div className="flex items-center space-x-3">
            <Button
              onClick={() => setAutoRefresh(!autoRefresh)}
              variant={autoRefresh ? "default" : "outline"}
              size="sm"
            >
              <RefreshCw className={`mr-2 h-4 w-4 ${autoRefresh ? 'animate-spin' : ''}`} />
              {autoRefresh ? 'Auto-Refresh ON' : 'Auto-Refresh OFF'}
            </Button>
            <Button 
              onClick={() => importMutation.mutate()}
              disabled={importMutation.isPending}
              variant="outline"
              className="text-purple-600 border-purple-600 hover:bg-purple-50"
            >
              <Upload className="mr-2 h-4 w-4" />
              {importMutation.isPending ? 'Importing...' : 'Import CSV'}
            </Button>
            <Button 
              onClick={() => syncMutation.mutate()}
              disabled={syncMutation.isPending}
              className="bg-blue-600 hover:bg-blue-700"
            >
              <RefreshCw className={`mr-2 h-4 w-4 ${syncMutation.isPending ? 'animate-spin' : ''}`} />
              {syncMutation.isPending ? 'Syncing...' : 'Sync Real-time'}
            </Button>
          </div>
        </div>
      </header>

      <div className="p-6 space-y-6">
        {/* Summary Statistics Cards */}
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-5 gap-4">
          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium text-slate-600">Total Students</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-slate-900">{analyticsData.summaryStats.totalStudents}</div>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium text-green-600">Improved</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-green-600">{analyticsData.summaryStats.improved}</div>
              <p className="text-xs text-slate-500">
                {((analyticsData.summaryStats.improved / analyticsData.summaryStats.totalStudents) * 100).toFixed(1)}%
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium text-red-600">Declined</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-red-600">{analyticsData.summaryStats.declined}</div>
              <p className="text-xs text-slate-500">
                {((analyticsData.summaryStats.declined / analyticsData.summaryStats.totalStudents) * 100).toFixed(1)}%
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium text-gray-600">Unchanged</CardTitle>
            </CardHeader>
            <CardContent>
              <div className="text-2xl font-bold text-gray-600">{analyticsData.summaryStats.same}</div>
              <p className="text-xs text-slate-500">
                {((analyticsData.summaryStats.same / analyticsData.summaryStats.totalStudents) * 100).toFixed(1)}%
              </p>
            </CardContent>
          </Card>

          <Card>
            <CardHeader className="pb-2">
              <CardTitle className="text-sm font-medium text-blue-600">Avg Improvement</CardTitle>
            </CardHeader>
            <CardContent>
              <div className={`text-2xl font-bold ${analyticsData.summaryStats.averageImprovement >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                {analyticsData.summaryStats.averageImprovement >= 0 ? '+' : ''}{analyticsData.summaryStats.averageImprovement}
              </div>
              <p className="text-xs text-slate-500">problems solved</p>
            </CardContent>
          </Card>
        </div>

        {/* Charts Row 1 */}
        <div className="grid grid-cols-1 xl:grid-cols-2 gap-6">
          {/* Progress Trend Chart */}
          <Card>
            <CardHeader>
              <CardTitle>Top 10 Students Progress Trend</CardTitle>
              <p className="text-sm text-slate-500">Historical progress across all weeks including current data</p>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={300}>
                <LineChart data={formatTrendData()}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" />
                  <YAxis />
                  <Tooltip />
                  <Legend />
                  {analyticsData.top10Students.map((_, index) => (
                    <Line
                      key={index}
                      type="monotone"
                      dataKey={`Week ${index + 1}`}
                      stroke={`hsl(${(index * 360) / 10}, 70%, 50%)`}
                      strokeWidth={2}
                    />
                  ))}
                </LineChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>

          {/* Improvement Analysis */}
          <Card>
            <CardHeader>
              <CardTitle>Top 15 Students with Most Improvement</CardTitle>
              <p className="text-sm text-slate-500">Students with highest problem count increases since Week 3</p>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={300}>
                <BarChart data={formatImprovementData()}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="name" angle={-45} textAnchor="end" height={80} />
                  <YAxis />
                  <Tooltip 
                    formatter={(value, name) => [
                      `${value} problems`,
                      name === 'improvement' ? 'Improvement' : 'Percentage'
                    ]}
                  />
                  <Bar dataKey="improvement" fill={COLORS.primary} />
                </BarChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </div>

        {/* Charts Row 2 */}
        <div className="grid grid-cols-1 xl:grid-cols-2 gap-6">
          {/* Progress Categories Pie Chart */}
          <Card>
            <CardHeader>
              <CardTitle>Progress Categories Distribution</CardTitle>
              <p className="text-sm text-slate-500">Breakdown of students by improvement status</p>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={300}>
                <PieChart>
                  <Pie
                    data={formatPieData()}
                    cx="50%"
                    cy="50%"
                    labelLine={false}
                    label={({ name, value, percent }) => `${name}: ${value} (${(percent * 100).toFixed(0)}%)`}
                    outerRadius={80}
                    fill="#8884d8"
                    dataKey="value"
                  >
                    {formatPieData().map((entry, index) => (
                      <Cell key={`cell-${index}`} fill={PIE_COLORS[index % PIE_COLORS.length]} />
                    ))}
                  </Pie>
                  <Tooltip />
                </PieChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>

          {/* Class Average Progress */}
          <Card>
            <CardHeader>
              <CardTitle>Class Average Progress Over Time</CardTitle>
              <p className="text-sm text-slate-500">Overall class performance progression</p>
            </CardHeader>
            <CardContent>
              <ResponsiveContainer width="100%" height={300}>
                <AreaChart data={analyticsData.classAverageProgression}>
                  <CartesianGrid strokeDasharray="3 3" />
                  <XAxis dataKey="week" />
                  <YAxis />
                  <Tooltip formatter={(value) => [`${value} problems`, 'Class Average']} />
                  <Area 
                    type="monotone" 
                    dataKey="average" 
                    stroke={COLORS.secondary} 
                    fill={COLORS.secondary}
                    fillOpacity={0.3}
                  />
                </AreaChart>
              </ResponsiveContainer>
            </CardContent>
          </Card>
        </div>

        {/* Detailed Comparison Table */}
        <Card>
          <CardHeader>
            <CardTitle>Detailed Student Comparison Table</CardTitle>
            <p className="text-sm text-slate-500">Comprehensive view of all students with historical and current data</p>
          </CardHeader>
          <CardContent>
            <div className="overflow-x-auto">
              <Table>
                <TableHeader>
                  <TableRow>
                    <TableHead>Student Name</TableHead>
                    <TableHead>LeetCode Username</TableHead>
                    <TableHead>Week 1</TableHead>
                    <TableHead>Week 2</TableHead>
                    <TableHead>Week 3</TableHead>
                    <TableHead>Current</TableHead>
                    <TableHead>Total Change</TableHead>
                    <TableHead>Improvement %</TableHead>
                    <TableHead>Status</TableHead>
                  </TableRow>
                </TableHeader>
                <TableBody>
                  {analyticsData.allStudentsData.map((student, index) => {
                    const week1 = student.weeklyTrends[0]?.totalProblems || 0;
                    const week2 = student.weeklyTrends[1]?.totalProblems || 0;
                    const week3 = student.weeklyTrends[2]?.totalProblems || 0;
                    const current = student.currentSolved;

                    return (
                      <TableRow key={index}>
                        <TableCell className="font-medium">{student.student.name}</TableCell>
                        <TableCell className="text-blue-600">
                          <a 
                            href={student.student.leetcodeProfileLink} 
                            target="_blank" 
                            rel="noopener noreferrer"
                            className="hover:underline"
                          >
                            {student.student.leetcodeUsername}
                          </a>
                        </TableCell>
                        <TableCell>{week1}</TableCell>
                        <TableCell>{week2}</TableCell>
                        <TableCell>{week3}</TableCell>
                        <TableCell className="font-semibold">{current}</TableCell>
                        <TableCell className={`font-medium ${student.improvement >= 0 ? 'text-green-600' : 'text-red-600'}`}>
                          {student.improvement >= 0 ? '+' : ''}{student.improvement}
                        </TableCell>
                        <TableCell className={student.improvementPercent >= 0 ? 'text-green-600' : 'text-red-600'}>
                          {student.improvementPercent >= 0 ? '+' : ''}{student.improvementPercent}%
                        </TableCell>
                        <TableCell>
                          <div className="flex items-center space-x-2">
                            {getStatusIcon(student.status)}
                            <Badge className={getStatusBadge(student.status)} variant="secondary">
                              {student.status}
                            </Badge>
                          </div>
                        </TableCell>
                      </TableRow>
                    );
                  })}
                </TableBody>
              </Table>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/pages/badges.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import { Input } from "@/components/ui/input";
import { Search, Trophy, Users, TrendingUp } from "lucide-react";
import { BADGE_TYPES } from "@/lib/constants";
import { useState } from "react";
import type { Badge as BadgeType } from "@shared/schema";

interface BadgeWithStudent extends BadgeType {
  student: {
    id: string;
    name: string;
    leetcodeUsername: string;
  };
}

interface BadgesPageData {
  allBadges: BadgeWithStudent[];
  badgeStats: {
    totalBadges: number;
    totalRecipients: number;
    mostPopularBadge: string;
    recentBadges: BadgeWithStudent[];
  };
}

export default function BadgesPage() {
  const [searchTerm, setSearchTerm] = useState("");
  const [selectedBadgeType, setSelectedBadgeType] = useState<string>("all");

  const { data, isLoading } = useQuery<BadgesPageData>({
    queryKey: ['/api/badges/all'],
  });

  if (isLoading) {
    return (
      <div className="flex-1 p-6">
        <div className="max-w-7xl mx-auto">
          <div className="animate-pulse space-y-6">
            <div className="h-8 bg-slate-200 rounded w-48"></div>
            <div className="grid grid-cols-1 md:grid-cols-3 gap-6">
              {[1, 2, 3].map((i) => (
                <div key={i} className="h-32 bg-slate-200 rounded"></div>
              ))}
            </div>
            <div className="h-96 bg-slate-200 rounded"></div>
          </div>
        </div>
      </div>
    );
  }

  if (!data) {
    return (
      <div className="flex-1 p-6">
        <div className="max-w-7xl mx-auto">
          <h1 className="text-3xl font-bold text-slate-900 mb-8">ğŸ… Badge System</h1>
          <div className="text-center py-12">
            <p className="text-slate-600">No badge data available</p>
          </div>
        </div>
      </div>
    );
  }

  const badgeTypes = Object.keys(BADGE_TYPES) as Array<keyof typeof BADGE_TYPES>;

  const filteredBadges = data.allBadges.filter(badge => {
    const matchesSearch = badge.student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         badge.student.leetcodeUsername.toLowerCase().includes(searchTerm.toLowerCase()) ||
                         badge.title.toLowerCase().includes(searchTerm.toLowerCase());
    const matchesType = selectedBadgeType === "all" || badge.badgeType === selectedBadgeType;
    return matchesSearch && matchesType;
  });

  const groupedBadges = badgeTypes.reduce((acc, badgeType) => {
    acc[badgeType] = data.allBadges.filter(badge => badge.badgeType === badgeType);
    return acc;
  }, {} as Record<string, BadgeWithStudent[]>);

  return (
    <div className="flex-1 p-6">
      <div className="max-w-7xl mx-auto">
        {/* Header */}
        <div className="mb-8">
          <h1 className="text-3xl font-bold text-slate-900 mb-2">ğŸ… Badge System</h1>
          <p className="text-slate-600">
            Auto-assigned badges recognizing student achievements and milestones
          </p>
        </div>

        {/* Stats Overview */}
        <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-8">
          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="w-12 h-12 bg-blue-50 rounded-lg flex items-center justify-center mr-4">
                  <Trophy className="w-6 h-6 text-blue-600" />
                </div>
                <div>
                  <p className="text-sm text-slate-600">Total Badges</p>
                  <p className="text-2xl font-bold text-slate-900">{data.badgeStats.totalBadges}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="w-12 h-12 bg-green-50 rounded-lg flex items-center justify-center mr-4">
                  <Users className="w-6 h-6 text-green-600" />
                </div>
                <div>
                  <p className="text-sm text-slate-600">Badge Recipients</p>
                  <p className="text-2xl font-bold text-slate-900">{data.badgeStats.totalRecipients}</p>
                </div>
              </div>
            </CardContent>
          </Card>

          <Card>
            <CardContent className="p-6">
              <div className="flex items-center">
                <div className="w-12 h-12 bg-purple-50 rounded-lg flex items-center justify-center mr-4">
                  <TrendingUp className="w-6 h-6 text-purple-600" />
                </div>
                <div>
                  <p className="text-sm text-slate-600">Most Popular</p>
                  <p className="text-lg font-bold text-slate-900">
                    {BADGE_TYPES[data.badgeStats.mostPopularBadge as keyof typeof BADGE_TYPES]?.title || 'N/A'}
                  </p>
                </div>
              </div>
            </CardContent>
          </Card>
        </div>

        {/* Badge Types Overview */}
        <Card className="mb-8">
          <CardHeader>
            <CardTitle>Badge Types</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {badgeTypes.map((badgeType) => {
                const badgeInfo = BADGE_TYPES[badgeType];
                const count = groupedBadges[badgeType]?.length || 0;

                return (
                  <div key={badgeType} className="flex items-center p-4 border rounded-lg hover:bg-slate-50">
                    <div className={`w-16 h-16 rounded-full flex items-center justify-center mr-4 bg-gradient-to-br ${badgeInfo.gradient} text-white`}>
                      <span className="text-2xl">{badgeInfo.emoji}</span>
                    </div>
                    <div className="flex-1">
                      <h3 className="font-semibold text-slate-900">{badgeInfo.title}</h3>
                      <p className="text-sm text-slate-600 mb-2">{badgeInfo.description}</p>
                      <Badge variant="secondary">{count} earned</Badge>
                    </div>
                  </div>
                );
              })}
            </div>
          </CardContent>
        </Card>

        {/* Tabs for different views */}
        <Tabs defaultValue="all-badges" className="space-y-6">
          <TabsList className="grid w-full grid-cols-3">
            <TabsTrigger value="all-badges">All Badges</TabsTrigger>
            <TabsTrigger value="by-type">By Type</TabsTrigger>
            <TabsTrigger value="recent">Recent</TabsTrigger>
          </TabsList>

          <TabsContent value="all-badges" className="space-y-6">
            {/* Search and Filter */}
            <div className="flex flex-col sm:flex-row gap-4">
              <div className="relative flex-1">
                <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-slate-400 w-4 h-4" />
                <Input
                  placeholder="Search students or badges..."
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>
              <select
                value={selectedBadgeType}
                onChange={(e) => setSelectedBadgeType(e.target.value)}
                className="px-3 py-2 border border-slate-300 rounded-md focus:ring-2 focus:ring-blue-500 focus:border-blue-500"
              >
                <option value="all">All Badge Types</option>
                {badgeTypes.map(badgeType => (
                  <option key={badgeType} value={badgeType}>
                    {BADGE_TYPES[badgeType].title}
                  </option>
                ))}
              </select>
            </div>

            {/* All Badges List */}
            <Card>
              <CardHeader>
                <CardTitle>All Badges ({filteredBadges.length})</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {filteredBadges.map((badge) => {
                    const badgeInfo = BADGE_TYPES[badge.badgeType as keyof typeof BADGE_TYPES];
                    return (
                      <div key={badge.id} className="flex items-center p-4 border rounded-lg hover:bg-slate-50">
                        <div className={`w-12 h-12 rounded-full flex items-center justify-center mr-4 bg-gradient-to-br ${badgeInfo.gradient} text-white`}>
                          <span className="text-lg">{badgeInfo.emoji}</span>
                        </div>
                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-1">
                            <h3 className="font-semibold text-slate-900">{badgeInfo.title}</h3>
                            <Badge variant="outline">{badge.student.name}</Badge>
                          </div>
                          <p className="text-sm text-slate-600">{badgeInfo.description}</p>
                        </div>
                        <div className="text-right text-sm text-slate-500">
                          {badge.earnedAt ? new Date(badge.earnedAt).toLocaleDateString() : 'N/A'}
                        </div>
                      </div>
                    );
                  })}
                  {filteredBadges.length === 0 && (
                    <div className="text-center py-8 text-slate-500">
                      No badges found matching your criteria
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>

          <TabsContent value="by-type" className="space-y-6">
            {badgeTypes.map((badgeType) => {
              const badgeInfo = BADGE_TYPES[badgeType];
              const badges = groupedBadges[badgeType] || [];

              return (
                <Card key={badgeType}>
                  <CardHeader>
                    <div className="flex items-center">
                      <div className={`w-12 h-12 rounded-full flex items-center justify-center mr-4 bg-gradient-to-br ${badgeInfo.gradient} text-white`}>
                        <span className="text-lg">{badgeInfo.emoji}</span>
                      </div>
                      <div>
                        <CardTitle>{badgeInfo.title}</CardTitle>
                        <p className="text-sm text-slate-600">{badgeInfo.description}</p>
                      </div>
                      <Badge variant="secondary" className="ml-auto">{badges.length} earned</Badge>
                    </div>
                  </CardHeader>
                  <CardContent>
                    {badges.length > 0 ? (
                      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                        {badges.map((badge) => (
                          <div key={badge.id} className="flex items-center p-3 border rounded-lg">
                            <div className="w-8 h-8 bg-slate-100 rounded-full flex items-center justify-center mr-3">
                              <span className="text-xs font-semibold text-slate-600">
                                {badge.student.name.split(' ').map(n => n[0]).join('')}
                              </span>
                            </div>
                            <div className="flex-1">
                              <p className="font-medium text-sm">{badge.student.name}</p>
                              <p className="text-xs text-slate-500">
                                {badge.earnedAt ? new Date(badge.earnedAt).toLocaleDateString() : 'N/A'}
                              </p>
                            </div>
                          </div>
                        ))}
                      </div>
                    ) : (
                      <p className="text-slate-500 text-center py-4">No one has earned this badge yet</p>
                    )}
                  </CardContent>
                </Card>
              );
            })}
          </TabsContent>

          <TabsContent value="recent" className="space-y-6">
            <Card>
              <CardHeader>
                <CardTitle>Recent Badges</CardTitle>
              </CardHeader>
              <CardContent>
                <div className="space-y-4">
                  {data.badgeStats.recentBadges.map((badge) => {
                    const badgeInfo = BADGE_TYPES[badge.badgeType as keyof typeof BADGE_TYPES];
                    return (
                      <div key={badge.id} className="flex items-center p-4 border rounded-lg hover:bg-slate-50">
                        <div className={`w-12 h-12 rounded-full flex items-center justify-center mr-4 bg-gradient-to-br ${badgeInfo.gradient} text-white`}>
                          <span className="text-lg">{badgeInfo.emoji}</span>
                        </div>
                        <div className="flex-1">
                          <div className="flex items-center gap-2 mb-1">
                            <h3 className="font-semibold text-slate-900">{badgeInfo.title}</h3>
                            <Badge variant="outline">{badge.student.name}</Badge>
                          </div>
                          <p className="text-sm text-slate-600">{badgeInfo.description}</p>
                        </div>
                        <div className="text-right text-sm text-slate-500">
                          {badge.earnedAt ? new Date(badge.earnedAt).toLocaleDateString() : 'N/A'}
                        </div>
                      </div>
                    );
                  })}
                  {data.badgeStats.recentBadges.length === 0 && (
                    <div className="text-center py-8 text-slate-500">
                      No recent badges found
                    </div>
                  )}
                </div>
              </CardContent>
            </Card>
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/leaderboard.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { Trophy, Medal, Award } from "lucide-react";
import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import type { AdminDashboardData } from "@shared/schema";

export default function Leaderboard() {
  const { data, isLoading, error } = useQuery<AdminDashboardData['leaderboard']>({
    queryKey: ['/api/leaderboard'],
  });

  if (error) {
    return (
      <div className="flex-1 p-6">
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <h3 className="text-red-800 font-medium">Error loading leaderboard</h3>
          <p className="text-red-600 text-sm mt-1">Failed to load leaderboard data.</p>
        </div>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="flex-1 p-6">
        <div className="animate-pulse space-y-4">
          <div className="h-8 bg-slate-200 rounded w-1/3"></div>
          {[...Array(10)].map((_, i) => (
            <div key={i} className="h-16 bg-slate-200 rounded"></div>
          ))}
        </div>
      </div>
    );
  }

  if (!data || data.length === 0) {
    return (
      <div className="flex-1 p-6">
        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
          <h3 className="text-yellow-800 font-medium">No leaderboard data</h3>
          <p className="text-yellow-600 text-sm mt-1">No student progress data available yet.</p>
        </div>
      </div>
    );
  }

  const getRankIcon = (rank: number) => {
    if (rank === 1) return <Trophy className="text-yellow-500" size={20} />;
    if (rank === 2) return <Medal className="text-slate-400" size={20} />;
    if (rank === 3) return <Award className="text-amber-600" size={20} />;
    return <span className="font-bold text-slate-600">{rank}</span>;
  };

  const getRankStyle = (rank: number) => {
    if (rank === 1) return 'bg-gradient-to-r from-yellow-50 to-orange-50 border-yellow-200';
    if (rank === 2) return 'bg-gradient-to-r from-slate-50 to-gray-50 border-slate-200';
    if (rank === 3) return 'bg-gradient-to-r from-amber-50 to-orange-50 border-amber-200';
    return 'bg-slate-50 border-slate-200';
  };

  return (
    <div className="flex-1 overflow-auto">
      {/* Header */}
      <header className="bg-white border-b border-slate-200 px-6 py-4">
        <div>
          <h2 className="text-xl font-semibold text-slate-900">Leaderboard</h2>
          <p className="text-sm text-slate-500">Weekly performance rankings</p>
        </div>
      </header>

      {/* Leaderboard Content */}
      <div className="p-6">
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Trophy className="text-yellow-500" size={24} />
              Weekly Champions
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {data.map((entry) => (
                <div 
                  key={entry.student.id}
                  className={`flex items-center justify-between p-4 rounded-lg border transition-all hover:shadow-md ${getRankStyle(entry.rank)}`}
                >
                  <div className="flex items-center space-x-4">
                    <div className="w-10 h-10 flex items-center justify-center">
                      {getRankIcon(entry.rank)}
                    </div>
                    <img 
                      src="https://images.unsplash.com/photo-1507003211169-0a1dd7228f2d?ixlib=rb-4.0.3&ixid=MnwxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8&auto=format&fit=crop&w=40&h=40"
                      alt="Student" 
                      className="w-10 h-10 rounded-full object-cover" 
                    />
                    <div>
                      <p className="font-semibold text-slate-900 text-lg">{entry.student.name}</p>
                      <p className="text-sm text-slate-500">@{entry.student.leetcodeUsername}</p>
                    </div>
                  </div>
                  <div className="text-right">
                    <p className="text-2xl font-bold text-slate-900">+{entry.weeklyScore}</p>
                    <p className="text-sm text-slate-500">problems this week</p>
                    {entry.student.stats?.ranking && (
                      <p className="text-xs text-gray-400 mt-1">
                        LeetCode Rank: #{entry.student.stats.ranking.toLocaleString()}
                      </p>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

