client/src/pages/student-dashboard.tsx.content:
import { useQuery } from "@tanstack/react-query";
import { useParams } from "wouter";
import { Link } from "wouter";
import { RefreshCw, ArrowLeft, ExternalLink } from "lucide-react";
import { Button } from "@/components/ui/button";
import { useToast } from "@/hooks/use-toast";
import { apiRequest } from "@/lib/queryClient";
import StatsOverview from "@/components/dashboard/stats-overview";
import DifficultyBreakdown from "@/components/dashboard/difficulty-breakdown";
import WeeklyProgress from "@/components/dashboard/weekly-progress";
import RecentBadges from "@/components/dashboard/recent-badges";
import DailyActivity from "@/components/dashboard/daily-activity";
import SubmissionStats from "@/components/dashboard/submission-stats";
import ActivityHeatmap from "@/components/dashboard/activity-heatmap";
import RankingOverview from "@/components/dashboard/ranking-overview";
import type { StudentDashboardData } from "@shared/schema";

export default function StudentDashboard() {
  const { username } = useParams();
  const { toast } = useToast();

  const { data, isLoading, error, refetch } = useQuery<StudentDashboardData>({
    queryKey: ['/api/dashboard/student', username],
    enabled: !!username,
  });

  const handleSync = async () => {
    try {
      if (data?.student) {
        await apiRequest('POST', `/api/sync/student/${data.student.id}`);
        await refetch();
        toast({
          title: "Sync successful",
          description: "Your LeetCode data has been updated.",
        });
      }
    } catch (error) {
      toast({
        title: "Sync failed",
        description: "Failed to sync LeetCode data. Please try again.",
        variant: "destructive",
      });
    }
  };

  if (!username) {
    return (
      <div className="flex-1 p-6">
        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
          <h3 className="text-lg font-medium text-yellow-800">No student selected</h3>
          <p className="text-yellow-700">Please go back and select a student from the directory.</p>
          <Link href="/">
            <Button className="mt-3">
              <ArrowLeft className="h-4 w-4 mr-2" />
              Back to Student Directory
            </Button>
          </Link>
        </div>
      </div>
    );
  }

  if (error) {
    return (
      <div className="flex-1 p-6">
        <div className="bg-red-50 border border-red-200 rounded-lg p-4">
          <h3 className="text-red-800 font-medium">Error loading dashboard</h3>
          <p className="text-red-600 text-sm mt-1">
            Failed to load student data. Please check if the student exists or try refreshing.
          </p>
        </div>
      </div>
    );
  }

  if (isLoading) {
    return (
      <div className="flex-1 p-6">
        <div className="animate-pulse space-y-6">
          <div className="h-8 bg-slate-200 rounded w-1/3"></div>
          <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
            {[...Array(4)].map((_, i) => (
              <div key={i} className="h-32 bg-slate-200 rounded"></div>
            ))}
          </div>
        </div>
      </div>
    );
  }

  if (!data) {
    return (
      <div className="flex-1 p-6">
        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-4">
          <h3 className="text-yellow-800 font-medium">No data available</h3>
          <p className="text-yellow-600 text-sm mt-1">
            No student data found. Please initialize the students first.
          </p>
        </div>
      </div>
    );
  }

  return (
    <div className="flex-1 overflow-auto">
      {/* Header */}
      <header className="bg-white border-b border-slate-200 px-6 py-4">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <Link href="/">
              <Button variant="ghost" size="sm">
                <ArrowLeft className="h-4 w-4 mr-2" />
                All Students
              </Button>
            </Link>
            <div>
              <h2 className="text-xl font-semibold text-slate-900">
                {data?.student?.name || username}
              </h2>
              <div className="flex items-center space-x-2">
                <p className="text-sm text-slate-500">@{username}</p>
                {data?.student?.leetcodeProfileLink && (
                  <a 
                    href={data.student.leetcodeProfileLink} 
                    target="_blank" 
                    rel="noopener noreferrer"
                    className="text-sm text-blue-600 hover:text-blue-800 flex items-center"
                  >
                    <ExternalLink className="h-3 w-3 ml-1" />
                  </a>
                )}
              </div>
            </div>
          </div>
          <div className="flex items-center space-x-4">
            <div className="text-sm text-slate-500">
              <RefreshCw className="inline mr-1" size={14} />
              Last sync: 2 min ago
            </div>
            <Button onClick={handleSync} className="bg-leetcode-primary hover:bg-blue-600">
              <RefreshCw className="mr-2" size={16} />
              Sync Now
            </Button>
          </div>
        </div>
      </header>

      {/* Dashboard Content */}
      <div className="p-6 space-y-6">
        <StatsOverview data={data} />

        <RankingOverview data={data} />

        <SubmissionStats data={data} />

        <div className="grid grid-cols-1 lg:grid-cols-2 gap-6">
          <DifficultyBreakdown data={data} />
          <WeeklyProgress data={data} />
        </div>

        <ActivityHeatmap data={data} />
        <RecentBadges data={data} />
        <DailyActivity data={data} />
      </div>
    </div>
  );
}

client/src/pages/student-directory.tsx.content:
import { useQuery } from '@tanstack/react-query';
import { Link } from 'wouter';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Input } from '@/components/ui/input';
import { Badge } from '@/components/ui/badge';
import { Avatar, AvatarFallback, AvatarImage } from '@/components/ui/avatar';
import { useState } from 'react';
import { Search, Trophy, TrendingUp } from 'lucide-react';

interface Student {
  id: string;
  name: string;
  leetcodeUsername: string;
  leetcodeProfileLink: string;
  profilePhoto?: string;
  createdAt: string;
}

interface StudentWithStats extends Student {
  stats: {
    totalSolved: number;
    easySolved: number;
    mediumSolved: number;
    hardSolved: number;
    ranking: number;
  };
  weeklyProgress: number;
  status: string;
  streak: number;
}

export default function StudentDirectory() {
  const [searchTerm, setSearchTerm] = useState('');

  const { data: students, isLoading } = useQuery<StudentWithStats[]>({
    queryKey: ['/api/students/all'],
  });

  const filteredStudents = students?.filter(student =>
    student.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
    student.leetcodeUsername.toLowerCase().includes(searchTerm.toLowerCase())
  ) || [];

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'Excellent': return 'bg-green-100 text-green-800 dark:bg-green-900 dark:text-green-300';
      case 'Active': return 'bg-blue-100 text-blue-800 dark:bg-blue-900 dark:text-blue-300';
      case 'Underperforming': return 'bg-red-100 text-red-800 dark:bg-red-900 dark:text-red-300';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-900 dark:text-gray-300';
    }
  };

  if (isLoading) {
    return (
      <div className="space-y-6">
        <div className="text-center">
          <h1 className="text-3xl font-bold">Student Directory</h1>
          <p className="text-muted-foreground">Loading students...</p>
        </div>
        <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
          {Array.from({ length: 12 }).map((_, i) => (
            <Card key={i} className="animate-pulse">
              <CardHeader>
                <div className="h-4 bg-gray-200 rounded w-3/4"></div>
                <div className="h-3 bg-gray-200 rounded w-1/2"></div>
              </CardHeader>
              <CardContent>
                <div className="h-3 bg-gray-200 rounded w-full"></div>
              </CardContent>
            </Card>
          ))}
        </div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="text-center">
        <h1 className="text-3xl font-bold">Student Directory</h1>
        <p className="text-muted-foreground">
          Browse all {students?.length || 0} students and their progress
        </p>
      </div>

      <div className="relative">
        <Search className="absolute left-3 top-3 h-4 w-4 text-muted-foreground" />
        <Input
          placeholder="Search students by name or LeetCode username..."
          value={searchTerm}
          onChange={(e) => setSearchTerm(e.target.value)}
          className="pl-10"
        />
      </div>

      <div className="grid gap-4 md:grid-cols-2 lg:grid-cols-3">
        {filteredStudents.map((student) => (
          <Link key={student.id} href={`/student/${student.leetcodeUsername}`}>
            <Card className="h-full hover:shadow-lg transition-shadow cursor-pointer border-2 hover:border-primary/20">
              <CardHeader className="pb-3">
                <div className="flex items-start justify-between">
                  <div className="flex items-center space-x-3">
                    <Avatar>
                      {student.profilePhoto && (
                        <AvatarImage src={student.profilePhoto} alt={student.name} />
                      )}
                      <AvatarFallback className="bg-primary/10">
                        {student.name.split(' ').map(n => n[0]).join('').toUpperCase()}
                      </AvatarFallback>
                    </Avatar>
                    <div>
                      <CardTitle className="text-base">{student.name}</CardTitle>
                      <CardDescription className="text-sm">
                        @{student.leetcodeUsername}
                      </CardDescription>
                    </div>
                  </div>
                  <Badge className={getStatusColor(student.status)}>
                    {student.status}
                  </Badge>
                </div>
              </CardHeader>
              <CardContent className="pt-0">
                <div className="space-y-3">
                  <div className="flex items-center justify-between text-sm">
                    <span className="flex items-center">
                      <Trophy className="h-4 w-4 mr-1 text-yellow-500" />
                      Total Solved
                    </span>
                    <span className="font-semibold">{student.stats.totalSolved}</span>
                  </div>

                  <div className="flex items-center justify-between text-sm">
                    <span className="flex items-center">
                      <TrendingUp className="h-4 w-4 mr-1 text-green-500" />
                      Weekly Progress
                    </span>
                    <span className="font-semibold">{student.weeklyProgress}</span>
                  </div>

                  <div className="flex items-center justify-between text-sm">
                    <span className="flex items-center">
                      <Trophy className="h-4 w-4 mr-1 text-yellow-500" />
                      LeetCode Rank
                    </span>
                    <span className="font-semibold text-xs">
                      {student.stats.ranking > 0 ? `#${student.stats.ranking.toLocaleString()}` : 'Not ranked'}
                    </span>
                  </div>

                  <div className="grid grid-cols-3 gap-2 text-xs">
                    <div className="text-center p-2 bg-green-50 dark:bg-green-900/20 rounded">
                      <div className="font-semibold text-green-700 dark:text-green-300">
                        {student.stats.easySolved}
                      </div>
                      <div className="text-green-600 dark:text-green-400">Easy</div>
                    </div>
                    <div className="text-center p-2 bg-yellow-50 dark:bg-yellow-900/20 rounded">
                      <div className="font-semibold text-yellow-700 dark:text-yellow-300">
                        {student.stats.mediumSolved}
                      </div>
                      <div className="text-yellow-600 dark:text-yellow-400">Medium</div>
                    </div>
                    <div className="text-center p-2 bg-red-50 dark:bg-red-900/20 rounded">
                      <div className="font-semibold text-red-700 dark:text-red-300">
                        {student.stats.hardSolved}
                      </div>
                      <div className="text-red-600 dark:text-red-400">Hard</div>
                    </div>
                  </div>
                </div>
              </CardContent>
            </Card>
          </Link>
        ))}
      </div>

      {filteredStudents.length === 0 && searchTerm && (
        <div className="text-center py-12">
          <Search className="h-12 w-12 text-muted-foreground mx-auto mb-4" />
          <h3 className="text-lg font-semibold">No students found</h3>
          <p className="text-muted-foreground">
            Try adjusting your search term or browse all students
          </p>
        </div>
      )}
    </div>
  );
}

