      icon: Flame,
      bgColor: "bg-orange-50",
      iconColor: "text-orange-500",
    },
    {
      label: "Active Days",
      value: data.totalActiveDays,
      change: `${((data.totalActiveDays / 365) * 100).toFixed(1)}%`,
